import React, { useState, useEffect } from "react";
import Likes from "../utils/Likes";
import Comments from "../utils/Comments";
import { useNavigate, Link } from "react-router-dom";
import Nav from "./Nav";

const Home = () => {
  const [sortByDate, setSortByDate] = useState(false);
  const [sortByAnswerCount, setSortByAnswerCount] = useState(false);
  const [sortOrder, setSortOrder] = useState("asc");
  const [sortedThreads, setSortedThreads] = useState({
    answered: [],
    unanswered: [],
  });
  const [refresh, setRefresh] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const checkUser = () => {
      if (!localStorage.getItem("_id")) {
        navigate("/");
      } else {
        fetch("http://localhost:4000/api/all/threads")
          .then((res) => res.json())
          .then((data) => {
            const answeredThreads = data.threads.filter(
              (thread) => thread.replies.length > 0
            );
            const unansweredThreads = data.threads.filter(
              (thread) => thread.replies.length === 0
            );
            setSortedThreads({
              answered: answeredThreads,
              unanswered: unansweredThreads,
            });
          })
          .catch((err) => console.error(err));
      }
    };
    checkUser();
  }, [navigate, refresh]);

  useEffect(() => {
    // Sort threads based on the selected options
    let sortedAnsweredThreads = [...sortedThreads.answered];
    let sortedUnansweredThreads = [...sortedThreads.unanswered];

    if (sortByDate) {
      sortedAnsweredThreads.sort((a, b) => {
        const dateA = new Date(a.timestamp);
        const dateB = new Date(b.timestamp);

        if (sortOrder === "asc") {
          return dateA - dateB;
        } else {
          return dateB - dateA;
        }
      });

      sortedUnansweredThreads.sort((a, b) => {
        const dateA = new Date(a.timestamp);
        const dateB = new Date(b.timestamp);

        if (sortOrder === "asc") {
          return dateA - dateB;
        } else {
          return dateB - dateA;
        }
      });
    }

    if (sortByAnswerCount) {
      sortedAnsweredThreads.sort((a, b) => {
        if (sortOrder === "asc") {
          return a.replies.length - b.replies.length;
        } else {
          return b.replies.length - a.replies.length;
        }
      });

      sortedUnansweredThreads.sort((a, b) => {
        if (sortOrder === "asc") {
          return a.replies.length - b.replies.length;
        } else {
          return b.replies.length - a.replies.length;
        }
      });
    }

    setSortedThreads({
      answered: sortedAnsweredThreads,
      unanswered: sortedUnansweredThreads,
    });
  }, [sortByDate, sortByAnswerCount, sortOrder, sortedThreads]);

  const deleteThread = (threadId) => {
    fetch(`http://localhost:4000/api/delete/thread/${threadId}`, {
      method: "DELETE",
    })
      .then((res) => res.json())
      .then((data) => {
        setRefresh((prevRefresh) => !prevRefresh);
        alert(data.message);
      })
      .catch((error) => {
        console.error("Error deleting thread:", error);
      });
  };

  const handleEdit = (threadId) => {
    const newText = prompt("Enter the new text:");
    if (newText) {
      editThread(threadId, newText);
    }
  };

  const editThread = (threadId, newText) => {
    fetch(`http://localhost:4000/api/edit/thread/${threadId}`, {
      method: "PUT",
      body: JSON.stringify({
        newText,
      }),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.thread) {
          const updatedAnsweredThreads = sortedThreads.answered.map((thread) =>
            thread.id === threadId ? data.thread : thread
          );
          const updatedUnansweredThreads = sortedThreads.unanswered.map(
            (thread) => (thread.id === threadId ? data.thread : thread)
          );
          setSortedThreads({
            answered: updatedAnsweredThreads,
            unanswered: updatedUnansweredThreads,
          });
          alert("Thread edited successfully!");
        } else {
          throw new Error(data.error_message);
        }
      })
      .catch((error) => {
        console.error("Error editing thread:", error);
      });
  };

  const handleSortByDate = () => {
    setSortByDate(true);
    setSortByAnswerCount(false);
  };

  const handleSortByAnswerCount = () => {
    setSortByDate(false);
    setSortByAnswerCount(true);
  };

  const handleSortOrder = (order) => {
    setSortOrder(order);
  };

  return (
    <>
      <Nav />
      <main className="home">
        <h2 className="homeTitle">Create a Thread</h2>
        {/* Create Thread Form */}
        <div className="thread__container">
          <div className="sort__buttons">
            <button
              onClick={handleSortByDate}
              className={sortByDate ? "modalBtn active" : "modalBtn"}
            >
              Sort by Date
            </button>
            <button
              onClick={handleSortByAnswerCount}
              className={sortByAnswerCount ? "modalBtn active" : "modalBtn"}
            >
              Sort by Answer Count
            </button>
            <button
              onClick={() => handleSortOrder("asc")}
              className={sortOrder === "asc" ? "modalBtn active" : "modalBtn"}
            >
              Ascending Order
            </button>
            <button
              onClick={() => handleSortOrder("desc")}
              className={sortOrder === "desc" ? "modalBtn active" : "modalBtn"}
            >
              Descending Order
            </button>
          </div>

          <h3>Answered Questions:</h3>
          {sortedThreads.answered.map((thread) => (
            <div className="thread__item" key={thread.id}>
              <p>{thread.title}</p>
              <div className="react__container">
                <Likes numberOfLikes={thread.likes.length} threadId={thread.id} />
                <Comments
                  numberOfComments={thread.replies.length}
                  threadId={thread.id}
                  title={thread.title}
                />
                {localStorage.getItem("_id") && (
                  <>
                    <button
                      className="modalBtn"
                      onClick={() => deleteThread(thread.id)}
                    >
                      Delete Thread
                    </button>
                    <button
                      className="modalBtn"
                      onClick={() => handleEdit(thread.id)}
                    >
                      Edit Thread
                    </button>
                  </>
                )}
              </div>
            </div>
          ))}

          <h3>Unanswered Questions:</h3>
          {sortedThreads.unanswered.map((thread) => (
            <div className="thread__item" key={thread.id}>
              <p>{thread.title}</p>
              <div className="react__container">
                <Likes numberOfLikes={thread.likes.length} threadId={thread.id} />
                <Comments
                  numberOfComments={thread.replies.length}
                  threadId={thread.id}
                  title={thread.title}
                />
                {localStorage.getItem("_id") && (
                  <>
                    <button
                      className="modalBtn"
                      onClick={() => deleteThread(thread.id)}
                    >
                      Delete Thread
                    </button>
                    <button
                      className="modalBtn"
                      onClick={() => handleEdit(thread.id)}
                    >
                      Edit Thread
                    </button>
                  </>
                )}
              </div>
            </div>
          ))}
        </div>
      </main>
    </>
  );
};

export default Home;
