import React, { useEffect, useState, useCallback } from "react";

const ReplyLikes = ({ numberOfLikes, replyId }) => {
  const [likesCount, setLikesCount] = useState(numberOfLikes);
  const [likes, setLikes] = useState([]);
  const [isLiked, setIsLiked] = useState(false);

  const handleLikeFunction = useCallback(async () => {
    try {
      const response = await fetch(
        `http://localhost:4000/api/like/reply/${replyId}`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            userId: localStorage.getItem("_id"),
          }),
        }
      );
      const data = await response.json();
      if (data.success) {
        setLikesCount(data.likesCount);
        setLikes(data.likes);
        setIsLiked(data.likes.includes(localStorage.getItem("_id")));
      }
    } catch (error) {
      console.error(error);
    }
  }, [replyId]);

  useEffect(() => {
    const fetchReplyLikesData = async () => {
      try {
        const response = await fetch(
          `http://localhost:4000/api/like/reply/${replyId}`
        );
        const data = await response.json();
        setLikesCount(data.likesCount);
        setLikes(data.likes);
        setIsLiked(data.likes.includes(localStorage.getItem("_id")));
      } catch (error) {
        console.error(error);
      }
    };

    fetchReplyLikesData();
  }, [replyId]);

  return (
    <div className="likes__container">
      <svg
        xmlns="http://www.w3.org/2000/svg"
        viewBox="0 0 24 24"
        fill={isLiked ? "#f99417" : "currentColor"}
        className="w-4 h-4 likesBtn"
        onClick={handleLikeFunction}
      >
        {/* SVG path */}
      </svg>
      <p style={{ color: "#434242" }}>{likesCount === 0 ? "" : likesCount}</p>
    </div>
  );
};

export default ReplyLikes;
