import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const QuestionAnswers = () => {
  const { id } = useParams();
  const [answers, setAnswers] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchAnswers = async () => {
      try {
        const response = await fetch(`http://localhost:4000/api/answers/${id}`);
        const data = await response.json();
        console.log("Data received from the server:", data); // Tikriname gautus duomenis
        setAnswers(data.answers);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching answers:", error);
      }
    };

    fetchAnswers();
  }, [id]);

  if (loading) {
    return <p>Loading question answers...</p>;
  }

  return (
    <div>
      <h1>Question Answers</h1>

      <div>
        {answers.map((answer) => (
          <div key={answer.id}>
            {/* Display answer information */}
            <h3>{answer.title}</h3>
            <p>{answer.content}</p>
            <p>Answered by: {answer.userId}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default QuestionAnswers;
