import React, { useEffect, useState } from "react";

const ReplyLikes = ({ numberOfLikes, replyId }) => {
  const [likesCount, setLikesCount] = useState(numberOfLikes);
  const [likes, setLikes] = useState([]); // Initialize the likes state as an empty array

  const handleLikeFunction = () => {
    fetch("http://localhost:4000/api/like/reply", {
      method: "POST",
      body: JSON.stringify({
        replyId,
        userId: localStorage.getItem("_id"),
      }),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.error_message) {
          alert(data.error_message);
        } else {
          setLikesCount(data.likesCount);
          setLikes(data.likes); // Update the likes state with the new likes array
        }
      })
      .catch((err) => console.error(err));
  };

  useEffect(() => {
    // Fetch initial likes data when the component mounts
    fetchLikesData();
  }, []);

  const fetchLikesData = () => {
    // Fetch likes data for the reply and update the likes state
    fetch(`http://localhost:4000/api/likes/reply/${replyId}`)
      .then((res) => res.json())
      .then((data) => {
        setLikesCount(data.likesCount);
        setLikes(data.likes);
      })
      .catch((error) => console.error("Error fetching likes data:", error));
  };

  return (
    <div className="likes__container">
      <svg
        xmlns="http://www.w3.org/2000/svg"
        viewBox="0 0 24 24"
        fill="currentColor"
        className="w-4 h-4 likesBtn"
        onClick={handleLikeFunction}
        style={{
          color: likes.includes(localStorage.getItem("_id"))
            ? "#f99417"
            : "#000",
        }}
      >
        {/* SVG path */}
      </svg>
      <p style={{ color: "#434242" }}>{likesCount === 0 ? "" : likesCount}</p>
    </div>
  );
};

export default ReplyLikes;
