import React, { useEffect, useState } from "react";
import { useParams, useNavigate, Link } from "react-router-dom";
import ReplyLikes from "../utils/ReplyLikes";

const Replies = () => {
  const [thread, setThread] = useState(null);
  const [replyList, setReplyList] = useState([]);
  const [reply, setReply] = useState("");
  const [title, setTitle] = useState("");
  const [isLoading, setIsLoading] = useState(true);
  const navigate = useNavigate();
  const { id } = useParams();
  const [replyLikes, setReplyLikes] = useState([]);

  const addReply = () => {
    fetch(`http://localhost:4000/api/add/reply/${id}`, {
      method: "POST",
      body: JSON.stringify({
        replyText: reply,
        userId: localStorage.getItem("_id"),
      }),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => {
        if (res.ok) {
          return res.json();
        } else {
          throw new Error("Failed to add reply");
        }
      })
      .then((data) => {
        if (data.success) {
          const newReply = {
            _id: data.reply._id,
            text: reply,
            name: "User",
            likes: [],
          };
          setReplyList((prevReplyList) => [...prevReplyList, newReply]);
          alert(data.message);
          navigate(`/${id}/replies`); // Navigate to the main page after successful reply submission
        } else {
          alert(data.message); // Display the error message
        }
      })
      .catch((err) => console.error(err));
  };

  const handleSubmitReply = (e) => {
    e.preventDefault();
    addReply();
    setReply("");
  };

  useEffect(() => {
    const fetchThread = () => {
      setIsLoading(true);

      fetch(`http://localhost:4000/api/thread/${id}`)
        .then((res) => {
          if (res.ok) {
            return res.json();
          } else {
            throw new Error("Failed to fetch thread");
          }
        })
        .then((data) => {
          setThread(data.thread);
          setReplyList(data.thread.replies || []); // Set replyList as an empty array if it's undefined
          setTitle(data.thread.title);
          setIsLoading(false);
        })
        .catch((error) => {
          console.error("Error fetching thread:", error);
          setIsLoading(false);
        });
    };

    fetchThread();
  }, [id]);

  const deleteReply = (replyId) => {
    fetch(`http://localhost:4000/api/delete/reply/${id}/${replyId}`, {
      method: "DELETE",
    })
      .then((res) => {
        if (res.ok) {
          return res.json();
        } else {
          throw new Error("Failed to delete reply");
        }
      })
      .then((data) => {
        if (data.success) {
          setReplyList((prevReplyList) =>
            prevReplyList.filter((reply) => reply._id !== replyId)
          );
          alert(data.message);
        } else {
          alert(data.message);
        }
      })
      .catch((error) => {
        console.error("Error deleting reply:", error);
      });
  };

  const editReply = (replyId) => {
    const newText = prompt("Enter the new text:");
    if (newText) {
      fetch(`http://localhost:4000/api/edit/reply/${id}/${replyId}`, {
        method: "PUT",
        body: JSON.stringify({
          newText,
        }),
        headers: {
          "Content-Type": "application/json",
        },
      })
        .then((res) => {
          if (res.ok) {
            return res.json();
          } else {
            throw new Error("Failed to edit reply");
          }
        })
        .then((data) => {
          if (data.success) {
            const updatedReplyList = replyList.map((reply) =>
              reply._id === replyId ? { ...reply, text: newText, edited: true } : reply
            );
            setReplyList(updatedReplyList);
            alert(data.message);
          } else {
            alert(data.message);
          }
        })
        .catch((error) => {
          console.error("Error editing reply:", error);
        });
    }
  };

  return (
    <main className="replies">
      <h1 className="repliesTitle">{title}</h1>

      {isLoading ? (
        <p>Loading...</p>
      ) : (
        thread && (
          <form className="modal__content" onSubmit={handleSubmitReply}>
            <label htmlFor="reply">Reply to the thread</label>
            <textarea
              rows={5}
              value={reply}
              onChange={(e) => setReply(e.target.value)}
              type="text"
              name="reply"
              className="modalInput"
            />

            <button className="modalBtn">SEND</button>
          </form>
        )
      )}
      <Link to="/dashboard">
        <button className="modalBtn">Create a Thread</button>
      </Link>
      <div className="thread__container">
        {replyList.map((reply) => (
          <div className="thread__item" key={reply._id}>
            <p>{reply.text}</p>
            <div className="react__container">
              <p style={{ opacity: "0.5" }}>by {reply.name}</p>
              <p style={{ opacity: "0.5" }}>{reply.timestamp}</p>

              {localStorage.getItem("_id") === reply.userId && (
                <>
                  <button
                    className="modalBtn"
                    onClick={() => deleteReply(reply._id)}
                  >
                    Delete
                  </button>
                  {!reply.edited && (
                    <button
                      className="modalBtn"
                      onClick={() => editReply(reply._id)}
                    >
                      Edit
                    </button>
                  )}
                </>
              )}
              <ReplyLikes
                numberOfLikes={reply.likes.length}
                replyId={reply._id}
              />
            </div>
          </div>
        ))}
      </div>
    </main>
  );
};

export default Replies;
