import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const Replies = () => {
  const [thread, setThread] = useState(null);
  const [replyList, setReplyList] = useState([]);
  const [reply, setReply] = useState("");
  const [title, setTitle] = useState("");
  const { id } = useParams();

  useEffect(() => {
    fetchThread();
  }, []);

  const fetchThread = () => {
    fetch(`http://localhost:4000/api/all/threads/${id}`)
      .then((res) => res.json())
      .then((data) => {
        const selectedThread = data.thread;
        if (selectedThread) {
          setThread(selectedThread);
          setReplyList(selectedThread.replies);
          setTitle(selectedThread.title);
        } else {
          setTitle("Thread not found");
        }
      })
      .catch((err) => console.error(err));
  };

  const addReply = () => {
    const newReply = {
      text: reply,
      userId: localStorage.getItem("_id"),
      likes: [],
      dislikes: [],
    };

    // Send the new reply to the server to save in the database
    fetch(`http://localhost:4000/api/add/reply/${id}`, {
      method: "POST",
      body: JSON.stringify(newReply),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((data) => {
        console.log(data.message);
        // Update the reply list with the new reply from the server
        if (data.reply) {
          setReplyList((prevReplyList) => [...prevReplyList, data.reply]);
        }
      })
      .catch((err) => console.error(err));

    setReply("");
  };

  const handleLikeReply = (replyId) => {
    // Send the like request to the server
    fetch(`http://localhost:4000/api/like-reply/${replyId}`, {
      method: "POST",
      body: JSON.stringify({
        userId: localStorage.getItem("_id"),
      }),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.message === "Reply liked successfully!") {
          const updatedReplyList = replyList.map((reply) => {
            if (reply._id === replyId) {
              reply.likes.push(localStorage.getItem("_id"));
            }
            return reply;
          });
          setReplyList(updatedReplyList);
        }
      })
      .catch((err) => console.error(err));
  };

  const handleDislikeReply = (replyId) => {
    // Send the dislike request to the server
    fetch(`http://localhost:4000/api/dislike-reply/${replyId}`, {
      method: "POST",
      body: JSON.stringify({
        userId: localStorage.getItem("_id"),
      }),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.message === "Reply disliked successfully!") {
          const updatedReplyList = replyList.map((reply) => {
            if (reply._id === replyId) {
              reply.dislikes.push(localStorage.getItem("_id"));
            }
            return reply;
          });
          setReplyList(updatedReplyList);
        }
      })
      .catch((err) => console.error(err));
  };

  return (
    <main className="replies">
      <h1 className="repliesTitle">{title}</h1>

      {localStorage.getItem("_id") && (
        <form className="modal__content" onSubmit={addReply}>
          <label htmlFor="reply">Reply to the thread</label>
          <textarea
            rows={5}
            value={reply}
            onChange={(e) => setReply(e.target.value)}
            type="text"
            name="reply"
            className="modalInput"
          />

          <button type="submit" className="modalBtn">
            SEND
          </button>
        </form>
      )}

      <div className="thread__container">
        {replyList.map((reply) => (
          <div className="thread__item" key={reply._id}>
            <div className="react__container">
              {localStorage.getItem("_id") && (
                <button
                  className="editBtn"
                  onClick={() => {
                    const newText = prompt("Enter the new text");
                    if (newText !== null) {
                      editReply(reply._id, newText);
                    }
                  }}
                >
                  Edit
                </button>
              )}
              <p style={{ opacity: "0.5" }}>by {reply.userId}</p>
              <p>{reply.text || "No reply text"}</p>
              <button
                className="likeBtn"
                onClick={() => handleLikeReply(reply._id)}
              >
                Like ({reply.likes.length})
              </button>
              <button
                className="dislikeBtn"
                onClick={() => handleDislikeReply(reply._id)}
              >
                Dislike ({reply.dislikes.length})
              </button>
            </div>
          </div>
        ))}
      </div>
    </main>
  );
};

export default Replies;
