import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

const Replies = () => {
  const [thread, setThread] = useState(null);
  const [replyList, setReplyList] = useState([]);
  const [reply, setReply] = useState("");
  const [title, setTitle] = useState("");
  const navigate = useNavigate();
  const { id } = useParams();

  const addReply = () => {
    // ... code to add the reply ...

    // Generate a unique ID for the reply
    const replyId = Math.floor(Math.random() * 1000000);

    // Create a new reply object with the unique ID, user, and timestamp
    const newReply = {
      id: replyId,
      text: reply,
      userId: localStorage.getItem("_id"),
      name: "User",
      timestamp: new Date().toLocaleString(),
    };

    // Update the reply list with the new reply
    setReplyList((prevReplyList) => [...prevReplyList, newReply]);
    setReply("");
  };

  const handleSubmitReply = (e) => {
    e.preventDefault();
    addReply();
  };

  const editReply = (replyId, newText) => {
    // Find the reply to edit based on the ID
    const updatedReplyList = replyList.map((reply) => {
      if (reply.id === replyId) {
        return {
          ...reply,
          text: newText,
        };
      }
      return reply;
    });

    setReplyList(updatedReplyList);
  };

  const deleteReply = (replyId) => {
    // Filter out the reply with the specified ID
    const updatedReplyList = replyList.filter((reply) => reply.id !== replyId);

    setReplyList(updatedReplyList);
  };

  const fetchThread = () => {
    fetch(`http://localhost:4000/api/thread/${id}`)
      .then((res) => res.json())
      .then((data) => {
        const { thread } = data;
        if (thread) {
          const { replies, title } = thread;

          const formattedReplies = replies.map((reply) => ({
            id: reply._id,
            text: reply.text,
            userId: reply.userId,
            name: reply.name,
            timestamp: reply.timestamp,
          }));

          setReplyList(formattedReplies);
          setTitle(title);
        } else {
          setTitle("Thread not found");
        }
      })
      .catch((error) => {
        console.error("Error retrieving thread:", error);
        setTitle("Error retrieving thread");
      });
  };

  useEffect(() => {
    if (thread === null) {
      fetchThread();
    }
  }, [id, thread]);

  return (
    <main className="replies">
      <h1 className="repliesTitle">{title}</h1>

      {localStorage.getItem("_id") && (
        <form className="modal__content" onSubmit={handleSubmitReply}>
          <label htmlFor="reply">Reply to the thread</label>
          <textarea
            rows={5}
            value={reply}
            onChange={(e) => setReply(e.target.value)}
            type="text"
            name="reply"
            className="modalInput"
          />

          <button className="modalBtn">SEND</button>
        </form>
      )}

      <div className="thread__container">
        {replyList.map((reply) => (
          <div className="thread__item" key={reply.id}>
            <p>{reply.text}</p>
            <div className="react__container">
              <p style={{ opacity: "0.5" }}>by {reply.name}</p>
              <p>{reply.timestamp}</p>
              {localStorage.getItem("_id") && (
                <>
                  <button
                    className="editBtn"
                    onClick={() => editReply(reply.id, "New text")}
                  >
                    Edit
                  </button>
                  <button
                    className="deleteBtn"
                    onClick={() => deleteReply(reply.id)}
                  >
                    Delete
                  </button>
                </>
              )}
            </div>
          </div>
        ))}
      </div>
    </main>
  );
};

export default Replies;
