import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

const Replies = () => {
  const [thread, setThread] = useState(null);
  const [replyList, setReplyList] = useState([]);
  const [reply, setReply] = useState("");
  const [title, setTitle] = useState("");
  const navigate = useNavigate();
  const { id } = useParams();

  const fetchThread = () => {
    fetch("http://localhost:4000/api/all/threads")
      .then((res) => res.json())
      .then((data) => {
        const selectedThread = data.threads.find((t) => t.id === id);
        if (selectedThread) {
          const updatedReplyList = selectedThread.replies.map((reply) => ({
            ...reply,
            likes: reply.likes || [],
            dislikes: reply.dislikes || [],
          }));

          setThread(selectedThread);
          setReplyList(updatedReplyList);
          setTitle(selectedThread.title);
        } else {
          setTitle("Thread not found");
        }
      })
      .catch((err) => console.error(err));
  };

  const addReply = () => {
    fetch(`http://localhost:4000/api/add/reply/${id}`, {
      method: "POST",
      body: JSON.stringify({
        replyText: reply,
        userId: localStorage.getItem("_id"),
      }),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((data) => {
        alert(data.message);
        fetchThread(); // Atnaujinti atsakymų duomenis iš serverio
        setReply(""); // Išvalyti atsakymo lauką
      })
      .catch((err) => console.error(err));
  };

  const editReply = (replyId, newText) => {
    fetch(`http://localhost:4000/api/edit-reply/${replyId}`, {
      method: "PATCH",
      body: JSON.stringify({ newText }),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.message === "Reply edited successfully!") {
          setReplyList((prevReplyList) =>
            prevReplyList.map((reply) => {
              if (reply._id === replyId) {
                reply.text = newText; // Atnaujinti teksto reikšmę
              }
              return reply;
            })
          );
        }
      })
      .catch((err) => console.error(err));
  };

  const handleSubmitReply = (e) => {
    e.preventDefault();
    addReply();
  };

  const handleEditReply = (replyId, newText) => {
    editReply(replyId, newText);
  };

  const likeReply = (replyId) => {
    fetch(`http://localhost:4000/api/like-reply/${replyId}`, {
      method: "POST",
      body: JSON.stringify({
        userId: localStorage.getItem("_id"),
      }),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.message === "Reply liked successfully!") {
          setReplyList((prevReplyList) =>
            prevReplyList.map((reply) => {
              if (reply._id === replyId) {
                reply.likes.push(localStorage.getItem("_id"));
              }
              return reply;
            })
          );
        }
      })
      .catch((err) => console.error(err));
  };

  const dislikeReply = (replyId) => {
    fetch(`http://localhost:4000/api/dislike-reply/${replyId}`, {
      method: "POST",
      body: JSON.stringify({
        userId: localStorage.getItem("_id"),
      }),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.message === "Reply disliked successfully!") {
          setReplyList((prevReplyList) =>
            prevReplyList.map((reply) => {
              if (reply._id === replyId) {
                reply.dislikes.push(localStorage.getItem("_id"));
              }
              return reply;
            })
          );
        }
      })
      .catch((err) => console.error(err));
  };

  useEffect(() => {
    if (thread === null) {
      fetchThread();
    }
  }, [id, thread]);

  return (
    <main className="replies">
      <h1 className="repliesTitle">{title}</h1>

      {localStorage.getItem("_id") && (
        <form className="modal__content" onSubmit={handleSubmitReply}>
          <label htmlFor="reply">Reply to the thread</label>
          <textarea
            rows={5}
            value={reply}
            onChange={(e) => setReply(e.target.value)}
            type="text"
            name="reply"
            className="modalInput"
          />

          <button className="modalBtn">SEND</button>
        </form>
      )}

      <div className="thread__container">
        {replyList.map((reply) => (
          <div className="thread__item" key={reply._id}>
            <div className="react__container">
              {localStorage.getItem("_id") && (
                <>
                  <button
                    className="editBtn"
                    onClick={() => handleEditReply(reply._id)}
                  >
                    Edit
                  </button>
                </>
              )}
              <p style={{ opacity: "0.5" }}>by {reply.name}</p>
              <p>
                {editingReplyId === reply._id ? (
                  <input
                    type="text"
                    value={reply.text}
                    onChange={(e) => setEditingReplyText(e.target.value)}
                  />
                ) : (
                  reply.text
                )}
              </p>
              <button className="likeBtn" onClick={() => likeReply(reply._id)}>
                Like
              </button>
              <button
                className="dislikeBtn"
                onClick={() => dislikeReply(reply._id)}
              >
                Dislike
              </button>
            </div>
          </div>
        ))}
      </div>
    </main>
  );
};

export default Replies;
